	input   enable;
	output   [9:0]  q;
	input   sclr;
	input   shiftin;
`ifndef ALTERA_RESERVED_QIS
// synopsys translate_off
`endif
	tri1   enable;
	tri0   sclr;
	tri1   shiftin;
`ifndef ALTERA_RESERVED_QIS
// synopsys translate_on
`endif

	reg	[9:0]	shift_reg;
	wire  [9:0]  shift_node;

	// synopsys translate_off
	initial
		shift_reg = 0;
	// synopsys translate_on
	always @ ( posedge clock)
		if (enable == 1'b1) 
			if (sclr == 1'b1) shift_reg <= 10'b0;
			else  shift_reg <= shift_node;
	assign
		q = shift_reg,
		shift_node = {shift_reg[8:0], shiftin};
