module shiftreg (
	clock,
	enable,
	sclr,
	shiftin,
	q)/* synthesis synthesis_clearbox = 1 */;

	input	  clock;
	input	  enable;
	input	  sclr;
	input	  shiftin;
	output	[9:0]  q;

	wire [9:0] sub_wire0;
	wire [9:0] q = sub_wire0[9:0];

	shiftreg_shift_reg	shiftreg_shift_reg_component (
				.clock (clock),
				.sclr (sclr),
				.enable (enable),
				.shiftin (shiftin),
				.q (sub_wire0));

endmodule

